document.addEventListener("DOMContentLoaded",()=>{
  loadQuestionBank();
  hydrateSettings();
  wireEvents();
  syncRangeControls();
  // Populate repo bank dropdown (banks/banks.json). Non-fatal if missing.
  initRepoBankDropdown();
  updateTimerVisibility(els.timerToggle.checked);
  els.start.disabled=false;
  els.qCount.max=String(questions.length);
  if(+els.qCount.value>questions.length) els.qCount.value=questions.length;
  updateBankStatus();
  updateAvailableHint();
  updateDomainSummary();
  els.quiz.innerHTML = `<div class="panel review"><strong>${questions.length}</strong> questions ready. Choose settings, then click <em>Start Quiz</em> or <em>Start Flashcards</em>.</div>`;
  renderAnalytics();
});

function wireEvents(){
  els.start.addEventListener("click", function(){ startQuiz(); });
  els.startFlash.addEventListener("click", function(){ startFlashcards(); });
  els.reset.addEventListener("click", resetQuiz);
  els.timerToggle.addEventListener("change", e=>updateTimerVisibility(e.target.checked));
  els.themeBtn.addEventListener("click", function(){ toggleTheme(); });

  // Domain selection modal
  if(els.openDomainModal) els.openDomainModal.addEventListener("click", openDomainModal);
  if(els.closeDomainModal) els.closeDomainModal.addEventListener("click", closeDomainModal);
  if(els.applyDomainModal) els.applyDomainModal.addEventListener("click", function(){ closeDomainModal(); updateAvailableHint(); });
  if(els.domainModal) els.domainModal.addEventListener("close", updateDomainSummary);

  // Filters
  els.domainAll?.addEventListener("change", ()=>syncDomainChecks("all"));
  [els.domain1,els.domain2,els.domain3,els.domain4,els.domain5].forEach(elm=>{
    elm?.addEventListener("change", ()=>syncDomainChecks("individual"));
  });
  els.difficulty?.addEventListener("change", updateAvailableHint);
  els.favoritesOnly?.addEventListener("change", updateAvailableHint);
  els.qCount?.addEventListener("input", updateAvailableHint);
  // Range
  els.rangePreset?.addEventListener("change", ()=>{
    const v=String(els.rangePreset.value||"").trim();
    if(!v){
      if(els.rangeStart) els.rangeStart.value="";
      if(els.rangeEnd) els.rangeEnd.value="";
    }else{
      const m=v.match(/^(\d+)\s*-\s*(\d+)$/);
      if(m){
        if(els.rangeStart) els.rangeStart.value=m[1];
        if(els.rangeEnd) els.rangeEnd.value=m[2];
      }
    }
    _rangeAutoCount = true;
    updateAvailableHint();
  });
  els.rangeStart?.addEventListener("input", ()=>{ if(els.rangePreset) els.rangePreset.value=""; _rangeAutoCount = true; updateAvailableHint(); });
  els.rangeEnd?.addEventListener("input", ()=>{ if(els.rangePreset) els.rangePreset.value=""; _rangeAutoCount = true; updateAvailableHint(); });
  els.clearRangeBtn?.addEventListener("click", ()=>{
    if(els.rangeStart) els.rangeStart.value="";
    if(els.rangeEnd) els.rangeEnd.value="";
    if(els.rangePreset) els.rangePreset.value="";
    updateAvailableHint();
  });


  // CSV Import (single-file). If legacy inputs exist, they still work.
  els.loadSelectedBankBtn?.addEventListener("click", loadSelectedBankFromDropdown);
  els.bankSelect?.addEventListener("change", ()=>{
    const v=els.bankSelect.value;
    if(v==='embedded') importState.messages=["Selected: Embedded bank. Click Load Selected Bank to apply."];
    else importState.messages=[`Selected: ${v}. Click Load Selected Bank to apply.`];
    updateBankStatus();
  });

  els.importBank?.addEventListener("change", onImportBankSelected);
  els.importQuestions?.addEventListener("change", onImportBankSelected);
  els.importAnswers?.addEventListener("change", onImportAnswersSelected); // legacy optional
  els.applyImportBtn?.addEventListener("click", applyImportedBank);
  els.clearBankBtn?.addEventListener("click", clearCustomBank);

  document.addEventListener("keydown", handleKeys, {passive:false});
}

function hydrateSettings(){
  try{
    const s=JSON.parse(localStorage.getItem(STORE.settings)||"{}");
    if(s){
      if(typeof s.shuffle==="boolean") els.shuffle.checked=s.shuffle;
      if(typeof s.feedback==="boolean") els.feedback.checked=s.feedback;
      if(typeof s.timerToggle==="boolean") els.timerToggle.checked=s.timerToggle;
      if(typeof s.examMode==="boolean") els.examMode.checked=s.examMode;
      if(s.qCount) els.qCount.value=+s.qCount;
      if(s.timerMinutes) els.timerMinutes.value=+s.timerMinutes;
      if(s.seed) els.seedInput.value=String(s.seed);
      if(els.rangeStart && s.rangeStart!=null) els.rangeStart.value = String(s.rangeStart||"");
      if(els.rangeEnd && s.rangeEnd!=null) els.rangeEnd.value = String(s.rangeEnd||"");
      if(s.theme==="dark") document.documentElement.classList.add("dark");
      els.themeBtn.textContent=document.documentElement.classList.contains("dark")?"☀️":"🌙";
      els.themeBtn.setAttribute("aria-pressed", String(document.documentElement.classList.contains("dark")));
    }
  }catch{}
}
function persistSettings(){
  const p={shuffle:els.shuffle.checked,feedback:els.feedback.checked,timerToggle:els.timerToggle.checked,qCount:+els.qCount.value||10,timerMinutes:+els.timerMinutes.value||20,examMode:!!els.examMode.checked,seed:els.seedInput.value,rangeStart:(els.rangeStart&&els.rangeStart.value)?(+els.rangeStart.value||"" ):"",rangeEnd:(els.rangeEnd&&els.rangeEnd.value)?(+els.rangeEnd.value||"" ):"",theme:document.documentElement.classList.contains("dark")?"dark":"light"};
  try{ localStorage.setItem(STORE.settings, JSON.stringify(p)); }catch(_e){}
}

function toggleTheme(){
  const dark=document.documentElement.classList.toggle("dark");
  els.themeBtn.setAttribute("aria-pressed", String(dark));
  els.themeBtn.textContent=dark?"☀️":"🌙";
  persistSettings();
}

function openDomainModal(){
  if(!els.domainModal) return;
  if(typeof els.domainModal.showModal === "function"){
    els.domainModal.showModal();
  }else{
    els.domainModal.setAttribute("open","" );
  }
}

function closeDomainModal(){
  if(!els.domainModal) return;
  if(typeof els.domainModal.close === "function"){
    els.domainModal.close();
  }else{
    els.domainModal.removeAttribute("open");
  }
  updateDomainSummary();
}


