let importState={bank:null, answers:null, csvText:null, messages:[], loading:false, pendingApply:false};

let repoBanks=[]; // loaded from /banks/banks.json

async function initRepoBankDropdown(){
  if(!els.bankSelect) return;
  // Always include Embedded option
  try{
    const res=await fetch('banks/banks.json', {cache:'no-store'});
    if(res.ok){
      const data=await res.json();
      const arr = Array.isArray(data) ? data : (data && Array.isArray(data.banks) ? data.banks : []);
      repoBanks = arr.filter(b=>b && b.id && b.url);
    }
  }catch(_e){
    repoBanks = [];
  }

  // Build options
  const sel=els.bankSelect;
  sel.innerHTML = '';
  const optEmbedded=document.createElement('option');
  optEmbedded.value='embedded';
  optEmbedded.textContent='Embedded bank (built-in)';
  sel.appendChild(optEmbedded);

  repoBanks.forEach(b=>{
    const opt=document.createElement('option');
    opt.value=String(b.id);
    opt.textContent=b.label || b.name || b.id;
    sel.appendChild(opt);
  });

  // Restore preferred selection (if it exists in dropdown)
  let preferred='embedded';
  try{ preferred = localStorage.getItem(STORE.bankPreferred) || 'embedded'; }catch(_e){}
  const has = Array.from(sel.options).some(o=>o.value===preferred);
  sel.value = has ? preferred : 'embedded';
}

async function loadSelectedBankFromDropdown(){
  if(!els.bankSelect) return;
  const choice=String(els.bankSelect.value||'embedded');
  if(choice==='embedded'){
    repoBankMeta = null;
    try{ localStorage.removeItem(STORE.bankPreferred); }catch(_e){}
    clearCustomBank();
    importState.messages=["Using embedded bank."];
    updateBankStatus();
    return;
  }
  const entry = repoBanks.find(b=>String(b.id)===choice);
  if(!entry){
    importState.messages=["Selected bank not found in banks/banks.json."];
    updateBankStatus();
    return;
  }

  importState.messages=[`Loading repo bank: ${entry.label||entry.id}...`];
  updateBankStatus();

  try{
    const res=await fetch(String(entry.url), {cache:'no-store'});
    if(!res.ok) throw new Error(`HTTP ${res.status}`);
    const data=await res.json();
    const arr = Array.isArray(data) ? data : (data && Array.isArray(data.questions) ? data.questions : null);
    if(!arr || !arr.length) throw new Error('Bank JSON must be an array of questions or an object with questions[].');

    questions = arr.map((q,i)=>normalizeQuestion(q, i+1));
    bankSourceRuntime = 'repo';
    repoBankMeta = {id:String(entry.id), label:(entry.label||entry.name||entry.id), url:String(entry.url)};

    let saved=false;
    try{
      localStorage.setItem(STORE.bank, JSON.stringify({version:1, updatedAt:Date.now(), source:'repo', meta:repoBankMeta, questions}));
      localStorage.setItem(STORE.bankPreferred, String(entry.id));
      // clear any CSV fallback
      localStorage.removeItem(STORE.bankCsv);
      localStorage.removeItem(STORE.bankSource);
      saved=true;
    }catch(_e){}

    els.qCount.max=String(questions.length);
    if(+els.qCount.value>questions.length) els.qCount.value=questions.length;
    importState.bank=null; importState.answers=null; importState.csvText=null;
    importState.messages=[saved ? `Repo bank loaded and saved locally: ${repoBankMeta.label}` : `Repo bank loaded: ${repoBankMeta.label}`];
    updateBankStatus();
    updateAvailableHint();
    updateDomainSummary();
    els.quiz.innerHTML = `<div class="panel review"><strong>${questions.length}</strong> questions ready. Choose settings, then click <em>Start Quiz</em> or <em>Start Flashcards</em>.</div>`;
  }catch(err){
    importState.messages=[`Failed to load repo bank: ${err?.message||err}`];
    updateBankStatus();
  }
}

function normalizeQuestion(q, fallbackNum){
  const out={...q};
  out.qNum = Number(out.qNum||fallbackNum||0);
  // Support multiple input shapes (embedded, CSV/JSON import, repo-bank JSON)
  // Repo-bank JSON uses TitleCase keys like Domain, DomainName, Question.
  out.domain = Number(out.domain ?? out.Domain ?? 0) || 0;
  out.domainName = (out.domainName || out.domain_name || out.DomainName || "").toString();
  out.q = (out.q || out.question || out.Question || out.prompt || out.Prompt || "").toString();
  out.options = Array.isArray(out.options) ? out.options.slice(0,4) : [out.A,out.B,out.C,out.D].filter(v=>v!==undefined);
  while(out.options.length<4) out.options.push("");
    out.correctLetter = (out.correctLetter || out.correctAnswer || out.CorrectAnswer || "").toString().trim().toUpperCase();
  const _m = out.correctLetter.match(/[A-D]/);
  out.correctLetter = _m ? _m[0] : "";
  if(!out.correct && out.correctLetter){
    const idx=letterToIndex(out.correctLetter);
    out.correct = out.options[idx] || out.correct;
  }
  out.correct = out.correct || "";
  out.ref = out.ref || (out.domain ? `Domain ${out.domain}: ${out.domainName}` : "");
  out.difficulty = (out.difficulty || out.Difficulty || "Medium").toString().trim();
  out.objective = (out.objective || out.Objective || "").toString().trim();
  out.rationaleCorrect = out.rationaleCorrect || out.RationaleCorrect || out.explanation || "";
  out.rationaleA = out.rationaleA || out.RationaleA || "";
  out.rationaleB = out.rationaleB || out.RationaleB || "";
  out.rationaleC = out.rationaleC || out.RationaleC || "";
  out.rationaleD = out.rationaleD || out.RationaleD || "";
  return out;
}

function loadQuestionBank(){
  // Priority: saved JSON bank -> saved CSV bank -> embedded
  try{
    const raw=localStorage.getItem(STORE.bank);
    if(raw){
      const payload=JSON.parse(raw);
      if(payload && Array.isArray(payload.questions) && payload.questions.length){
        questions = payload.questions.map((q,i)=>normalizeQuestion(q, i+1));
        if(payload.source==='repo'){
          bankSourceRuntime = 'repo';
          repoBankMeta = payload.meta || null;
        }else{
          bankSourceRuntime = 'imported';
          repoBankMeta = null;
        }
        return;
      }
    }
  }catch(_e){}

  try{
    const src=localStorage.getItem(STORE.bankSource);
    const csv=localStorage.getItem(STORE.bankCsv);
    if(src==='csv' && csv){
      const {objs}=parseCSV(String(csv));
      questions = objs.map((r,i)=>rowToQuestion(r, i+1));
      bankSourceRuntime = 'imported';
      return;
    }
  }catch(_e){}

  questions = EMBEDDED_QUESTIONS.map((q,i)=>normalizeQuestion(q, i+1));
  bankSourceRuntime = 'embedded';
  repoBankMeta = null;
}


function clearCustomBank(){
  try{ localStorage.removeItem(STORE.bank); }catch(_e){}
  try{ localStorage.removeItem(STORE.bankCsv); }catch(_e){}
  try{ localStorage.removeItem(STORE.bankSource); }catch(_e){}
  try{ localStorage.removeItem(STORE.bankPreferred); }catch(_e){}
  importState={bank:null, answers:null, csvText:null, messages:[], loading:false, pendingApply:false};
  repoBankMeta = null;
  loadQuestionBank();
  els.qCount.max=String(questions.length);
  if(+els.qCount.value>questions.length) els.qCount.value=questions.length;
  updateBankStatus();
  updateAvailableHint();
  els.quiz.innerHTML = `<div class="panel review"><strong>${questions.length}</strong> questions ready. Choose settings, then click <em>Start Quiz</em> or <em>Start Flashcards</em>.</div>`;
}

function updateBankStatus(){
  if(!els.bankStatus) return;
  let label = "Embedded bank";
  if(bankSourceRuntime==='imported') label = "Imported bank (this session)";
  if(bankSourceRuntime==='repo') label = "Repo bank (this session)";
  try{
    if(localStorage.getItem(STORE.bank)){
      const raw=localStorage.getItem(STORE.bank);
      let src="imported";
      try{ src=(JSON.parse(raw||"{}").source)||"imported"; }catch(_e){}
      label = (src==='repo') ? "Repo bank (saved locally)" : "Imported bank (saved locally)";
    }
    else if(localStorage.getItem(STORE.bankSource)==='csv' && localStorage.getItem(STORE.bankCsv)) label = "Imported bank (CSV saved locally)";
  }catch(_e){}

  const msgs=[];
  if(bankSourceRuntime==='repo' && repoBankMeta && (repoBankMeta.label||repoBankMeta.id)){
    msgs.push(`Bank: ${repoBankMeta.label||repoBankMeta.id}`);
  }
  if(importState.bank) msgs.push(`Staged bank: ${importState.bank.length}`);
  if(importState.answers) msgs.push(`Staged answers: ${Object.keys(importState.answers).length}`);
  msgs.push(...(importState.messages||[]));

  if(bankSourceRuntime==='repo' && repoBankMeta && repoBankMeta.label){
    msgs.unshift(`Selected: ${repoBankMeta.label}`);
  }
  els.bankStatus.textContent = `${label} • Total loaded: ${questions.length}` + (msgs.length?` • ${msgs.join(" • ")}`:"");
}

function getSelectedDomains(){
  if(!els.domainAll) return null;
  if(els.domainAll.checked) return null;
  const ds=[
    els.domain1?.checked?1:null,
    els.domain2?.checked?2:null,
    els.domain3?.checked?3:null,
    els.domain4?.checked?4:null,
    els.domain5?.checked?5:null,
  ].filter(Boolean);
  return ds.length?ds:null;
}

function updateDomainSummary(){
  if(!els.domainSummary) return;
  const map={
    1:"Domain 1: General Security Concepts",
    2:"Domain 2: Threats, Vulnerabilities, and Mitigations",
    3:"Domain 3: Security Architecture",
    4:"Domain 4: Security Operations",
    5:"Domain 5: Security Program Management and Oversight"
  };
  const sel=getSelectedDomains();
  if(!sel){
    els.domainSummary.textContent = "All domains selected";
    return;
  }
  const parts=sel.map(d=>map[d] || ("Domain " + d));
  els.domainSummary.textContent = "Selected: " + parts.join(" • ");
}


function syncDomainChecks(mode){
  const inds=[els.domain1,els.domain2,els.domain3,els.domain4,els.domain5].filter(Boolean);
  if(mode==="all"){
    if(els.domainAll.checked){ inds.forEach(c=>c.checked=false); }
    else{
      // If user unchecks All, default to selecting everything (so they can then uncheck specific ones)
      inds.forEach(c=>c.checked=true);
    }
  }else{
    const any=inds.some(c=>c.checked);
    if(any) els.domainAll.checked=false;
    else els.domainAll.checked=true;
  }
  updateDomainSummary();
  updateAvailableHint();
}

function applyDomainDifficultyFilters(pool){
  let out=pool.slice();
  const domains=getSelectedDomains();
  if(domains) out=out.filter(q=>domains.includes(Number(q.domain||0)));
  const diff=(els.difficulty?.value||"ALL").toString();
  if(diff!=="ALL") out=out.filter(q=>String(q.difficulty||"Medium")==diff);
  return out;
}


let _rangePresetTotal = 0;
let _rangeAutoCount = false;


function rebuildRangePresets(total){
  if(!els.rangePreset) return;
  els.rangePreset.innerHTML="";
  const optAll=document.createElement("option");
  optAll.value="";
  optAll.textContent = `All (1-${total})`;
  els.rangePreset.appendChild(optAll);
  // Use 50-question blocks so it's easier to work through the (large) bank in chunks.
  const step=50;
  for(let s=1;s<=total;s+=step){
    const e=Math.min(total, s+step-1);
    const opt=document.createElement("option");
    opt.value=`${s}-${e}`;
    opt.textContent=`${s}-${e}`;
    els.rangePreset.appendChild(opt);
  }
}

function getSelectedRange(){
  const total = questions.length||0;
  if(!total) return null;
  const sRaw = els.rangeStart ? String(els.rangeStart.value||"").trim() : "";
  const eRaw = els.rangeEnd ? String(els.rangeEnd.value||"").trim() : "";
  if(!sRaw && !eRaw) return null;
  let s = sRaw?parseInt(sRaw,10):1;
  let e = eRaw?parseInt(eRaw,10):total;
  if(isNaN(s)) s=1;
  if(isNaN(e)) e=total;
  s=Math.max(1, Math.min(total, s));
  e=Math.max(1, Math.min(total, e));
  if(s>e){ const t=s; s=e; e=t; }
  return {start:s,end:e};
}

function applyRangeFilter(pool){
  const r=getSelectedRange();
  if(!r) return pool.slice();
  return pool.filter(q=>Number(q.qNum||0)>=r.start && Number(q.qNum||0)<=r.end);
}

function syncRangeControls(){
  const total = questions.length||0;
  if(els.rangeStart) els.rangeStart.max = String(total||1);
  if(els.rangeEnd) els.rangeEnd.max = String(total||1);
  if(els.rangePreset && total && total!==_rangePresetTotal){
    rebuildRangePresets(total);
    _rangePresetTotal = total;
  }
  updateRangeSummary();
}

function updateRangeSummary(){
  if(!els.rangeSummary) return;
  const total = questions.length||0;
  const r=getSelectedRange();
  if(!r){
    els.rangeSummary.textContent = total ? `All questions (1–${total})` : 'All questions';
    return;
  }
  els.rangeSummary.textContent = `Range: ${r.start}–${r.end}`;
}
function updateAvailableHint(){
  // Reuse the status line so you always see what you'll actually be quizzing on.
  syncRangeControls();
  let pool=applyRangeFilter(questions);
  pool=applyDomainDifficultyFilters(pool);
  if(els.favoritesOnly && els.favoritesOnly.checked){
    const favs=getBookmarks(); pool = pool.filter(q=>favs.has(q.qNum));
  }
  updateBankStatus();
  if(!els.bankStatus) return;
  els.bankStatus.textContent += ` • After filters: ${pool.length}`;
  // If the user just changed the range controls, auto-set the question count
  // to the size of the selected range (capped by the filtered pool).
  if(_rangeAutoCount){
    const r = getSelectedRange();
    if(r && pool.length>0){
      const desired = Math.max(1, Math.min(pool.length, (r.end - r.start + 1)));
      els.qCount.value = String(desired);
    }
    _rangeAutoCount = false;
  }

  // Clamp question count to the filtered pool size
  if(+els.qCount.value>pool.length && pool.length>0) els.qCount.value=pool.length;
}

